type packedAndInternal struct {
	packed   string
	internal *bchain.EthereumInternalData
}

func unpackTxs(packed []packedAndInternal, parser bchain.BlockChainParser) []bchain.Tx {
	r := make([]bchain.Tx, len(packed))
	for i, p := range packed {
		b, err := hex.DecodeString(p.packed)
		if err != nil {
			panic(err)
		}
		tx, _, err := parser.UnpackTx(b)
		if err != nil {
			panic(err)
		}
		c, _ := tx.CoinSpecificData.(bchain.EthereumSpecificData)
		c.InternalData = p.internal
		tx.CoinSpecificData = c
		r[i] = *tx
	}
	return r
}

// GetTestEthereumTypeBlock1 returns block #1
func GetTestEthereumTypeBlock1(parser bchain.BlockChainParser) *bchain.Block {
	return &bchain.Block{
		BlockHeader: bchain.BlockHeader{
			Height:        4321000,
			Hash:          "0xc7b98df95acfd11c51ba25611a39e004fe56c8fdfc1582af99354fcd09c17b11",
			Size:          31839,
			Time:          1534858022,
			Confirmations: 2,
		},
		Txs: unpackTxs([]packedAndInternal{{
			packed: EthTx1Packed,
		}, {
			packed:   EthTx2Packed,
			internal: EthTx2InternalData,
		}}, parser),
		CoinSpecificData: Block1SpecificData,
	}
}

