func unpackTxs(packed []string, parser bchain.BlockChainParser) []bchain.Tx {
	r := make([]bchain.Tx, len(packed))
	for i, p := range packed {
		b, err := hex.DecodeString(p)
		if err != nil {
			panic(err)
		}
		tx, _, err := parser.UnpackTx(b)
		if err != nil {
			panic(err)
		}
		r[i] = *tx
	}
	return r
}

